import React from 'react';
import {useState, useRef} from "react";
import Modal from "react-bootstrap/Modal"
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { Dialog, DialogTitle, DialogContent, makeStyles } from '@material-ui/core';
import CloseIcon from '@material-ui/icons/Close';
import "./auth.css";

function Copyright() {
    return (
        <Typography variant="body2" color="textSecondary" align="center">
            {'Copyright Â© '}
            <Link color="inherit" href="https://personal-site-a0cc2.firebaseapp.com/">
            Nguyen Cao Nhan
            </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const useStyles = makeStyles((theme) => ({
    root: {
        height: '100vh',
    },
    image: {
        backgroundImage: 'url(https://images-na.ssl-images-amazon.com/images/I/71k5kfdB9KL.png)',
        backgroundRepeat: 'no-repeat',
        backgroundColor:
        theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
        backgroundSize: 'cover',
        backgroundPosition: 'center',
    },
    paper: {
        margin: theme.spacing(8, 4),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
    dialogWrapper: {
        padding: theme.spacing(2),
        position: 'absolute',
        top: theme.spacing(5)
    },
    dialogTitle: {
        paddingRight: '0px'
    }
}));

export default function Auth() {
    const classes = useStyles();
    const [openPopup, setOpenPopup ] = React.useState(false);
    const [loginError, setLoginError] = React.useState("Error");
    const [registerError, setRegisterError] = React.useState("Error");
    
    //Login refs
    const username = useRef();
    const password = useRef();
    
    //Register refs
    const usernameCreate = useRef();
    const fullnameCreate = useRef();
    const passwordCreate = useRef();
    const passwordConfirm = useRef();

    const handleLogin = (e) => {
        e.preventDefault();
    }

    const handleRegister = (e) => {
        e.preventDefault();
    }

    return (
        <>
            <Grid container component="main" className={classes.root}>
                <CssBaseline />
                <Grid item xs={false} sm={4} md={7} className={classes.image} />
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={12} square>
                    <div className={classes.paper}>
                    <Avatar className={classes.avatar}>
                        <LockOutlinedIcon />
                    </Avatar>
                    <Typography component="h1" variant="h5">
                        ÄÄng nháº­p
                    </Typography>
                    <form onSubmit={handleLogin} className={classes.form}>
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="username"
                            label="TÃªn ÄÄng nháº­p"
                            name="username"
                            autoFocus
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="password"
                            label="Máº­t kháº©u"
                            name="password"
                            type="password"
                        />
                        <p style={{margin: 0, padding: 0, color: "red", fontStyle: "italic"}} visibility="hidden">{loginError}</p>
                        <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                        >
                        ÄÄng nháº­p
                        </Button>
                        <Grid container>
                        <Grid item xs>
                            <Link href="#" variant="body2">
                                QuÃªn máº­t kháº©u?
                            </Link>
                        </Grid>
                        <Grid item>
                            <Link href="#" onClick={() => setOpenPopup(true)}>
                                Táº¡o tÃ i khoáº£n
                            </Link>
                        </Grid>
                        </Grid>
                        <Box mt={5}>
                        <Copyright />
                        </Box>
                    </form>
                    </div>
                </Grid>
            </Grid>
            <Dialog open={openPopup} maxWidth="sm" fullWidth="true" classes={{ paper: classes.dialogWrapper }}>
                <DialogTitle className={classes.dialogTitle}>
                    <div style={{ display: 'flex' }}>
                        <Typography variant="h6" component="div" style={{ flexGrow: 1 }}>
                            ÄÄng kÃ½
                        </Typography>
                        <Button
                            color="primary"
                            onClick={()=>{setOpenPopup(false)}}>
                            <CloseIcon />
                        </Button>
                    </div>
                </DialogTitle>
                <DialogContent>
                    <form onSubmit={handleRegister} className={classes.form}>
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="username-create"
                            label="TÃªn ÄÄng nháº­p"
                            name="username-create"
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="fullname"
                            label="TÃªn Äáº§y Äá»§"
                            name="fullname"
                            type="text"
                        />
                        <Grid container>
                            <Grid item md={6} xs={12} className="leftSubGrid">
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="password-create"
                                    label="Máº­t kháº©u"
                                    name="password-create"
                                    type="password"
                                />
                            </Grid>
                            <Grid item md={6} xs={12} className="rightSubGrid">
                                <TextField
                                    variant="outlined"
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="password-confirm"
                                    label="XÃ¡c nháº­n máº­t kháº©u"
                                    name="password-confirm"
                                    type="password"
                                />
                            </Grid>
                        </Grid>
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="secondary"
                            className={classes.submit}>
                                ÄÄng kÃ½ tÃ i khoáº£n
                        </Button>
                    </form>
                </DialogContent>
            </Dialog>
        </>
    )
}
