import "./question-edit-modal.css";
import {React, useState, useEffect} from 'react'
import {Form, Button, Modal, Row, Col} from "react-bootstrap";
import {Cancel, PermMedia} from "@material-ui/icons"
import storage from './../../firebase';

function QuestionEditModal(props) {
    const [question, setQuestion] = useState("");
    const [questionType, setQuestionType] = useState(0);
    const [numberOfChoices, setNumberOfChoices] = useState(0);
    const [numberOfChoicesError, setNumberOfChoicesError] = useState("");
    const [keys, setKeys] = useState([]);
    const [keyCorrects, setKeyCorrects] = useState([]);
    const [explain, setExplain] = useState("");
    const [file, setFile] = useState(null);
    const [url, setURL] = useState(""); //This only be used in case of passing a Firebase URL from QuizCreate
    const [time, setTime] = useState("");
    const [point, setPoint] = useState("");
    const [validateError, setValidateError] = useState("");

    const handleQuestionTypeChange = (type) => {
        setQuestionType(type);
        if (type === 1) 
        {
            setNumberOfChoices(1);
            setNumberOfChoicesError("");
            setKeyCorrects([true]);
            setKeys([""]);
        } else {
            setNumberOfChoices(0);
            setKeyCorrects([]);
            setKeys([]);
        }
    }

    const handleChoicesCountChange = (count) => {
        if (count < 2 || count > 5) {
            setNumberOfChoicesError("Số lượng phương án phải từ 2 đến 5.");
        } else {
            setNumberOfChoices(count);
            setNumberOfChoicesError("");
            let tmpKeysArr = []
            let tmpKeysCorrectArr = []
            for (let i = 0; i < count; i++) {
                tmpKeysArr.push("");
                tmpKeysCorrectArr.push(false);
            }
            setKeyCorrects(tmpKeysCorrectArr);
            setKeys(tmpKeysArr);
        }
    }

    const handleCorrectKeyChange = (index) => {
        let getKeyCorrectArr = [];
        for (let i = 0; i < keyCorrects.length; i++) {
            getKeyCorrectArr.push(false);
        }
        let item = getKeyCorrectArr[index];
        getKeyCorrectArr[index] = !item;
        setKeyCorrects(getKeyCorrectArr);
    }

    const handleKeyChange = (index, value) => {
        let emptyArr = [];
        for (let i = 0; i < keys.length; i++) {
            if (i === index) {
                emptyArr.push(value);
            } else {
                emptyArr.push(keys[i]);
            }
        }
        setKeys(emptyArr);
    }

    const prepareFormSubmit = async(e) => {
        e.preventDefault();

        //Validate info
        if (question === "") {
            setValidateError("Vui lòng nhập nội dung câu hỏi.");
            return;
        } else if (keys.length === 0) {
            setValidateError("Vui lòng nhập đáp án cho câu hỏi.");
            return;
        } else if (keys.length !== 1 && !keyCorrects.includes(true)) {
            setValidateError("Vui lòng chọn 1 đáp án đúng.");
            return;
        } else if (!props.useQuizTime && isNaN(parseInt(time))) {
            setValidateError("Thời gian Quiz không hợp lệ.");
            return;
        } else if (isNaN(parseInt(point))) {
            setValidateError("Điểm số câu không hợp lệ.");
            return;
        }

        //Upload to firebase and retrieve URL 
        if (file) {
            let fileName = file.name + "-" + Date.now();
            //questions_images
            storage.ref(`questions_images/${fileName}`).put(file)
            .on("state_changed", (snapshot) => {}, 
            (error) => {console.log(error)}, 
            () => {
                storage.ref(`questions_images/`).child(`${fileName}`).getDownloadURL()
                .then(
                    (url) => {
                        //setImage(url);
                        callParentSubmit(url);
                    },
                    (error) => {
                        console.log(error);
                    }
                );
            });
        } else {
            callParentSubmit(url);
        }
    }

    const callParentSubmit = (image) => {
        let output = {};
        output["question"] = question;
        output["questionType"] = questionType;
        output["numberOfChoices"] = numberOfChoices;
        output["keys"] = keys;
        output["keyCorrects"] = keyCorrects;
        output["explain"] = explain;
        output["image"] = image;
        output["time"] = time === ""? 0 : parseInt(time);
        output["questIndex"] = props.questionEditGet["questIndex"];

        //Refresh
        setValidateError("");
        setQuestion("");
        setQuestionType(0);
        setNumberOfChoicesError("");
        setNumberOfChoices(0);
        setKeys([]);
        setKeyCorrects([]);
        setExplain("");
        setFile(null);
        setTime("");
        setURL("");

        props.onEditQuestionSubmit(output);
    }

    useEffect(() => {
        if (props.questionEditGet) {
            setQuestion(props.questionEditGet["question"]);
            setQuestionType(props.questionEditGet["questionType"]);
            setNumberOfChoices(props.questionEditGet["keys"].length);
            setKeys(props.questionEditGet["keys"]);
            setKeyCorrects(props.questionEditGet["keyCorrects"]);
            setTime(props.questionEditGet["time"]);
            setExplain(props.questionEditGet["explain"]);
            setURL(props.questionEditGet["image"]);
        }
    }, [props.questionEditGet]);

    return (
        <Modal
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            {...props}
        >
            <Modal.Header>
                <Modal.Title id="contained-modal-title-vcenter">
                    Sửa câu hỏi (câu {props.questionEditGet? props.questionEditGet["questIndex"] + 1 : ""})
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Row>
                    <Col md={10} sm={12} className="offset-md-1 offset-sm-0">
                        <Form id="questionAddForm" onSubmit={prepareFormSubmit}>
                            <Form.Group className="" controlId="formBasicEmail">
                                <Form.Label>Câu hỏi</Form.Label>
                                <Form.Control as="textarea" required validate="Quiz name is not valid!"
                                value={question}
                                onChange={(e) => setQuestion(e.target.value)}/>
                            </Form.Group>
                            <Form.Group className="">
                            <fieldset>
                                <Form.Group as={Row} className="mb-3">
                                <Form.Label as="legend" column sm={2}>
                                    Loại:
                                </Form.Label>
                                <Col sm={12}>
                                    <Form.Check
                                        type="radio"
                                        label="Trắc nghiệm"
                                        name="question-type"
                                        checked={questionType === 0}
                                        onChange={() => handleQuestionTypeChange(0)}
                                    />
                                     <Form.Check
                                        type="radio"
                                        label="Trả lời ngắn"
                                        name="question-type"
                                        checked={questionType === 1}
                                        onChange={() => handleQuestionTypeChange(1)}
                                    />
                                </Col>
                                </Form.Group>
                            </fieldset>
                            </Form.Group>
                            {questionType === 0 && 
                                (<Form.Group className="" controlId="formBasicEmail">
                                <Form.Label>Số phương án</Form.Label>
                                <Form.Control 
                                    type="number"
                                    validate="Phải là giá trị từ 2 đến 5" 
                                    min={2} max={5}
                                    value={numberOfChoices}
                                    onChange={(e) => handleChoicesCountChange(e.target.value)}
                                />
                                <p className="error">{numberOfChoicesError}</p>
                                </Form.Group>)
                            }
                            {
                                numberOfChoicesError === "" && (questionType === 0 && [...Array(parseInt(numberOfChoices))].map((x, i) => {
                                    let getClassName = `question-${numberOfChoices}-answer`;
                                    return (
                                        <Form.Group className="mt-2" controlId="formBasicEmail">
                                            <Form.Label>Phương án {i + 1}
                                            <Form.Check
                                                type="radio"
                                                label="Đúng"
                                                name={getClassName}
                                                checked={keyCorrects[i]}
                                                onChange={() => handleCorrectKeyChange(i)}
                                            />
                                            </Form.Label>
                                            <Form.Control type="text" required validate="Không hợp lệ!" minLength={1}
                                            value={keys[i]}
                                            onChange={(e) => handleKeyChange(i, e.target.value)}/>
                                        </Form.Group>
                                    )
                                })) || 
                                (questionType === 1 && 
                                    <Form.Group className="" controlId="formBasicEmail">
                                        <Form.Label>Đáp án</Form.Label>
                                        <Form.Control type="text" required validate="Đáp án không hợp lệ!" minLength={1}
                                        value={keys[0]}
                                        onChange={(e) => handleKeyChange(0, e.target.value)}/>
                                    </Form.Group>
                                )
                            }
                            <Form.Group className="mt-2" controlId="formBasicEmail">
                                <Form.Label>Giải thích (nếu có)</Form.Label>
                                <Form.Control as="textarea" validate="Không hợp lệ!"
                                value={explain}
                                onChange={(e) => setExplain(e.target.value)}/>
                            </Form.Group>
                            <Form.Group className="mt-2" controlId="formBasicEmail">
                                <Form.Label>Điểm số</Form.Label>
                                <Form.Control as="textarea" validate="Không hợp lệ!"
                                value={point}
                                onChange={(e) => setPoint(e.target.value)}/>
                            </Form.Group>
                            {!props.useQuizTime && <Form.Group controlId="formFileSm" className="mb-3 mt-3">
                                <Form.Label>Thời gian trả lời (giây): </Form.Label><br></br>
                                <Form.Control 
                                    type="number"
                                    min = {1}
                                    value={time}
                                    onChange={(e) => setTime(e.target.value)} 
                                />
                            </Form.Group>}
                            <Form.Group controlId="formFileSm" className="mb-3 mt-3">
                                <Form.Label>Ảnh (nếu có)</Form.Label><br></br>
                                <label htmlFor="file" className="shareOption" style={{cursor: "pointer"}}>
                                    <PermMedia htmlColor="green" className="shareIcon"/>
                                    <Form.Control style={{display: "none"}} type="file" id="file" accept=".png, .jpeg, .jpg" size="sm" onChange={(e) => setFile(e.target.files[0])}/>
                                </label>

                            </Form.Group>
                            </Form>
                            {(file || url !== "") && <div className="shareImgContainer">
                                <img className="shareImg" src={file && URL.createObjectURL(file) || url} alt=""/>
                                <Cancel className="shareCancelImg" onClick={() => setFile(null)}></Cancel>
                            </div>}
                    </Col>
                </Row>
            </Modal.Body>
            <Modal.Footer>
            <p className="error">{validateError}</p>
            <Button variant="warning" type="submit" form="questionAddForm">Sửa</Button>
            </Modal.Footer>
        </Modal>
    )
}

export default QuestionEditModal
