import {React, useState, useEffect, useContext, useRef} from 'react'
import "./quizmain.css";
import axios from "axios";
import {AuthContext} from "../../context/AuthContext";
import {
    useParams
} from "react-router-dom";
import {Row, Col, Container, ProgressBar, Button} from "react-bootstrap";
import { CountdownCircleTimer } from 'react-countdown-circle-timer';

function QuizMain() {
    let {id} = useParams();
    const {user} = useContext(AuthContext);
    const [questions, setQuestions] = useState([]);
    const [quizInfo, setQuizInfo] = useState([]);
    const [point, setPoint] = useState(0);
    const [currentQuest, setCurrentQuest] = useState(0);
    const [quizTime, setQuizTime] = useState(false)
    const [time, setTime] = useState(0);
    const [progressBarInterval, setProgressBarInterval] = useState(0);
    let percentage = 100;

    const shuffleArray = (array) => {
        for (var i = array.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = array[i];
            array[i] = array[j];
            array[j] = temp;
        }
        return array
    }

    const getQuestions = async() => {
        
    }

    const getQuizInfo = async() => {
        let quizInfo = await axios.get(`/quiz/info/${id}`);
        quizInfo = JSON.parse(quizInfo.request.response);
        setQuizInfo(quizInfo[0]);
        setQuizTime(quizInfo[0]["quiz_time"] != null);
        if (quizInfo[0]["quiz_time"] != null) {
            setTime(parseInt(quizInfo[0]["quiz_time"]))
        }

        let listOfQuestions = await axios.get(`/quiz/join/${id}`);
        listOfQuestions = JSON.parse(listOfQuestions.request.response);
        if (quizInfo["raw_order"] == 0) {
            listOfQuestions = shuffleArray(listOfQuestions);
        }
        setQuestions(listOfQuestions);
    }

    useEffect(() => {
        if (quizInfo.length == 0) {
            getQuizInfo();
            getQuestions();
        }
        console.log(percentage);
    }, [])

    useEffect(() => {
        if (currentQuest == 1) {
            percentage = 100;
            let progressBarInterval = setInterval(() => {
                percentage -= 1;
                console.log("I'm here")
                if (percentage == 0) {
                    clearInterval(progressBarInterval);
                    percentage = 0;
                }
            }, 150);
        } 
        if (currentQuest != 0 && currentQuest != 1 && quizTime) {
            percentage = 100;
            let progressBarInterval = setInterval(() => {
                percentage -= 1;
                if (percentage == 0) {
                    clearInterval(progressBarInterval);
                    percentage = 0;
                }
            }, 150);
        }
    }, [currentQuest])

    return (
        <>
            {questions.length == 0 && (<p>Loading Questions</p>)}
            {questions.length != 0 && (
                <Container className="mt-3 mb-3">
                    <h1 className="text-center">{quizInfo.quiz_title}</h1>
                    <Row className="">
                        <Col lg={4} md={12} sm={12}>
                            <h5 className="text-center">{user.username}</h5>
                        </Col>
                        <Col lg={4} md={6} sm={12}>
                            <h5 className="text-center">Câu hỏi: {currentQuest}/{questions.length}</h5>
                        </Col>
                        <Col lg={4} md={6} sm={12}>
                            <h5 className="text-center">Điểm: {point}</h5>
                        </Col>
                    </Row>
                    <h5 className="text-center">
                        Thời gian: {time}
                    </h5>
                    <div className="progress mb-2">
                        <ProgressBar 
                            variant="info"
                            striped 
                            now={percentage}
                            style={{width: '100%'}}
                        ></ProgressBar>
                        
                    </div>
                    <div className="text-center">
                        
                    </div>
                    {currentQuest == 0 && (
                        <div className="text-center">
                            <Button onClick={() => setCurrentQuest(1)} variant="info" className="text-white text-center">Bắt đầu</Button>
                        </div>
                    )}
                    {currentQuest != 0 && 
                        <>
                        <div className="question-box d-flex justify-content-center align-items-center">
                            <h5>{questions[currentQuest - 1].question_content}</h5>
                        </div>
                        {questions[currentQuest - 1].media != "" && <img src={questions[currentQuest - 1].media}></img>}
                        </>
                    }
                </Container>
            )}
        </>
    )
}

export default QuizMain
