import {React, useState, useEffect, useContext} from 'react'
import "./quizmain.css";
import axios from "axios";
import {AuthContext} from "../../context/AuthContext";
import {
    useParams
} from "react-router-dom";

function QuizMain() {
    let {id} = useParams();
    const {user} = useContext(AuthContext);
    const [questions, setQuestions] = useState([]);
    const [quizInfo, setQuizInfo] = useState([]);

    const shuffleArray = (array) => {
        for (var i = array.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = array[i];
            array[i] = array[j];
            array[j] = temp;
        }
        return array
    }

    const getQuestions = async() => {
        let listOfQuestions = await axios.get(`/quiz/join/${id}`);
        listOfQuestions = JSON.parse(listOfQuestions.request.response);
        console.log(quizInfo[0]);
        if (quizInfo[0]["raw_order"] == 0) {
            listOfQuestions = shuffleArray(listOfQuestions);
        }
        
        setQuestions(listOfQuestions);
    }

    const getQuizInfo = async() => {
        let quizInfo = await axios.get(`/quiz/info/${id}`);
        quizInfo = JSON.parse(quizInfo.request.response);
        setQuizInfo(quizInfo);
    }

    useEffect(() => {
        if (quizInfo.length == 0) {
            getQuizInfo();
        }
        if (questions.length == 0) {
            getQuestions();
        }
    }, [])

    useEffect(() => {
        console.log(questions);
        console.log(quizInfo);
    }, [questions, quizInfo])

    return (
        <>
            {questions.length == 0 && (<p>Loading Questions</p>)
            || questions.map(question => {
                return (
                    <p>Questions Loaded Completed</p>
                )
            })}
        </>
    )
}

export default QuizMain
