import "./home.css";
import {React, useState, useEffect, useContext} from 'react';
import axios from "axios";
import {AuthContext} from "../../context/AuthContext";

import QuizCreate from "./../quiz-create/QuizCreate";


export default function Home() {
    const {user} = useContext(AuthContext);
    const [mode, setMode] = useState(0);
    const [publicQuizzes, setPublicQuizzes] = useState([]);
    const [privateQuizzes, setPrivateQuizzes] = useState([]);

    const getQuizzes = async() => {
        let listOfQuizzes = await axios.get("/quiz/public");
        listOfQuizzes = JSON.parse(listOfQuizzes.request.response);
        let getPublic = [];
        let getPrivate = [];
        listOfQuizzes.map(quiz => {
            if (quiz.quiz_mode === 0) {
                getPublic.push(quiz);
            } else {
                getPrivate.push(quiz);
            }
        });
        setPublicQuizzes(getPublic);
        setPrivateQuizzes(getPrivate);
    }

    useEffect(() => {
        getQuizzes();    
    }, [])
    
    return (
        <>
            {/* Show public quizzes */}

        </>
    )
}
