import "./quizcreate.css";
import {React, useState} from 'react'

//Bootstrap
import {Container, Col, Row, Form, Button} from "react-bootstrap";

//Material UI icons
import {Add} from "@material-ui/icons";

export default function QuizCreate() {
    const [quizMode, setQuizMode] = useState(0);
    const [numberOfQuest, setNumberOfQuest] = useState(0)
    const [useQuizTime, setUseQuizTime] = useState(false);
    const [questionList, setQuestionlist] = useState([])

    const onQuizModeChange = (type) => {
        setQuizMode(type);
        
    }

    const addNewQuestion = () => {

    }

    return (
        <div>
            <Container className="p-4">
                <h1 className="text-center">Create a new quiz</h1>

                <Row>
                    <Col md={8} sm={12} className="offset-md-2 offset-sm-0">
                        <Form>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>Quiz Name</Form.Label>
                                <Form.Control type="text" required validate="Quiz name is not valid!"/>
                            </Form.Group>
                            <fieldset>
                                <Form.Group as={Row} className="mb-3">
                                <Form.Label as="legend" column sm={2}>
                                    Quiz Mode:
                                </Form.Label>
                                <Col sm={10}>
                                    <Form.Check
                                        type="radio"
                                        label="Public"
                                        name="quiz-mode"
                                        checked={quizMode == 0}
                                        onChange={() => onQuizModeChange(0)}
                                    />
                                     <Form.Check
                                        type="radio"
                                        label="Private"
                                        name="quiz-mode"
                                        checked={quizMode == 1}
                                        onChange={() => onQuizModeChange(1)}
                                    />
                                </Col>
                                </Form.Group>
                            </fieldset>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>Quiz Password</Form.Label>
                                <Form.Control 
                                    type="text"
                                    validate="Quiz password is not valid!" 
                                    minLength={6} maxLength={10}
                                    disabled = {quizMode == 0 && "disabled"}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Check 
                                    label="Quiz Time (minutes)" 
                                    onChange = {() => setUseQuizTime(!useQuizTime)} 
                                />
                                <Form.Control 
                                    type="number" placeholder="Quiz Time (minutes)"
                                    min = {1}
                                    validate="Quiz password is not valid!" 
                                    disabled = {!useQuizTime && "disabled"}
                                />
                            </Form.Group>
                            <Form.Group>
                                <Form.Check 
                                    label="Keep order of questions" 
                                />
                                <Form.Text muted>
                                    Otherwise, the questions will be randomed
                                </Form.Text>
                            </Form.Group>
                        </Form>
                        <Row className="mt-2">
                            <Button variant="primary" className="btn-block" onClick={() => addNewQuestion}>
                                <Add fontSize="small" style={{ color: "white"}}></Add>
                            </Button>
                            <p className="mt-2" >Number of Questions: {numberOfQuest}</p>
                            {
                                
                            }
                        </Row>
                    </Col>
                </Row>
            </Container>

            
        </div>
    )
}
