import "./quiz-review.css";
import {React, useState, useEffect, useRef} from 'react'
import {Form, Button, Modal, Row, Col} from "react-bootstrap";

function QuizReview(props) {
    const [markContent, setMarkContent] = useState([]);
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        setMarkContent(props.reviewContent);
        setQuestions(props.questions);
    }, [])

    useEffect(() => {
        console.log(markContent);
    }, [markContent])

    return (
        <>
            <Container className="mt-3 mb-3">
                <h1 className="text-center">{quizInfo.quiz_title}</h1>
                <h3 className="text-center">Tạo bởi: {quizInfo.creator}</h3>
                <Row className="">
                    <Col lg={4} md={12} sm={12}>
                        <h5 className="text-center">{user.username}</h5>
                    </Col>
                    <Col lg={4} md={6} sm={12}>
                        <h5 className="text-center">Câu hỏi: {currentQuest}/{questions.length}</h5>
                    </Col>
                    <Col lg={4} md={6} sm={12}>
                        <h5 className="text-center">Điểm: {point}</h5>
                    </Col>
                </Row>
            </Container>
        </>
    )
}

export default QuizReview
