import {React, useState, useEffect, useContext} from 'react'
import "./quizmain.css";
import axios from "axios";
import {AuthContext} from "../../context/AuthContext";
import {
    useParams
} from "react-router-dom";
import {Row, Col, Container, Card, Button} from "react-bootstrap";

function QuizMain() {
    let {id} = useParams();
    const {user} = useContext(AuthContext);
    const [questions, setQuestions] = useState([]);
    const [quizInfo, setQuizInfo] = useState([]);
    const [point, setPoint] = useState(0);
    const [currentQuest, setCurrentQuest] = useState(0);
    const [quizTime, setQuizTime] = useState(false)

    const shuffleArray = (array) => {
        for (var i = array.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = array[i];
            array[i] = array[j];
            array[j] = temp;
        }
        return array
    }

    const getQuestions = async() => {
        
    }

    const getQuizInfo = async() => {
        let quizInfo = await axios.get(`/quiz/info/${id}`);
        quizInfo = JSON.parse(quizInfo.request.response);
        setQuizInfo(quizInfo[0]);
        setQuizTime(quizInfo[0] != null);

        let listOfQuestions = await axios.get(`/quiz/join/${id}`);
        listOfQuestions = JSON.parse(listOfQuestions.request.response);
        if (quizInfo["raw_order"] == 0) {
            listOfQuestions = shuffleArray(listOfQuestions);
        }
        setQuestions(listOfQuestions);
    }

    useEffect(() => {
        if (quizInfo.length == 0) {
            getQuizInfo();
            getQuestions();
        }
    }, [])

    useEffect(() => {
        console.log(questions);
        console.log(quizInfo);
    }, [questions, quizInfo])

    return (
        <>
            {questions.length == 0 && (<p>Loading Questions</p>)}
            {questions.length != 0 && (
                <Container className="mt-3 mb-3">
                    <h1 className="text-center">{quizInfo.quiz_title}</h1>
                    <Row className="">
                        <Col lg={4} md={12} sm={12}>
                            <h5 className="text-center">{user.username}</h5>
                        </Col>
                        <Col lg={4} md={6} sm={12}>
                            <h5 className="text-center">Câu hỏi: {currentQuest}/{questions.length}</h5>
                        </Col>
                        <Col lg={4} md={6} sm={12}>
                            <h5 className="text-center">Điểm: {point}</h5>
                        </Col>
                    </Row>
                    <h5 className="text-center">
                        
                    </h5>
                    {currentQuest == 0 && (
                        <div className="text-center">
                            <Button onClick={() => setCurrentQuest(1)} variant="info" className="text-white text-center">Bắt đầu</Button>
                        </div>
                    )}
                    {currentQuest != 0 && 
                        <>
                        <div className="question-box d-flex justify-content-center align-items-center">
                            <h5>{questions[currentQuest - 1].question_content}</h5>
                        </div>
                        {questions[currentQuest - 1].media != "" && <img src={questions[currentQuest - 1].media}></img>}
                        </>
                    }
                </Container>
            )}
        </>
    )
}

export default QuizMain
